function [dectime,lat,long,psp,slvp,dryt,relhum,windspd,windavg,OZ]=metload(pathname,filename);%  [dd,lt,lg,psp,sp,dt,rh,ws,wa,cs]=metload(ddd)file_load=0;if nargin==2; file_load=1; end;if file_load  [hdr,data]=hdrload([pathname filename]);  dectime=data(:,1);  lat=data(:,2);  long=data(:,3);  slvp=data(:,4);  dryt=data(:,5);  relhum=data(:,6);  windspd=data(:,7);  winddir=data(:,8);  psp=data(:,9);  windavg=windspd;% else% % waitfor(met_gui)% dectime=(startinput:byinput/1440:toinput)';% lat=latinput* ones(size(dectime));% long=longinput* ones(size(dectime));% psp=Pspinput * ones(size(dectime));% slvp=slvpinput * ones(size(dectime));% dryt=Dryt * ones(size(dectime));% relhum=relhuminput * ones(size(dectime));% windspd=windspdinput * ones(size(dectime));% windavg=windavginput * ones(size(dectime));% OZ=Ozoneinput/1000; %enter in OZ in Atm*cm (divide DU by 1000), Use 999 for the model to use it's own computation (from Van Heukleton model1979)%   dectime=(249:(0.00546):250)';%   lat=29.86 * ones(size(dectime));%   long=87,1 * ones(size(dectime));%   psp=100 * ones(size(dectime));%   slvp=1013.25 * ones(size(dectime));%   dryt=0 * ones(size(dectime));%   relhum=80 * ones(size(dectime));%   windspd=0 * ones(size(dectime));%   windavg=0 * ones(size(dectime));%   OZ=285/1000; %enter in OZ in Atm*cm (divide DU by 1000), Use 999 for the model to use it's own computation (from Van Heukleton model1979)end